import React, { useState } from "react";

export default function AuthPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <div className="min-h-screen grid lg:grid-cols-2">

      {/* LEFT PANEL */}
      <div className="hidden lg:flex bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white items-center justify-center p-12">
        <div>
          <h1 className="text-4xl font-bold mb-4">LearnHub</h1>
          <p className="text-gray-300">
            The best work you can do is to start today.
          </p>
        </div>
      </div>

      {/* RIGHT FORM */}
      <div className="flex items-center justify-center bg-gray-50 p-6">

        <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl">

          <h2 className="text-2xl font-bold mb-6 text-gray-800">
            Sign in
          </h2>

          <div className="space-y-4">

            <input
              type="text"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full border px-4 py-3 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none"
            />

            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border px-4 py-3 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none"
            />

            <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition">
              Sign In
            </button>

          </div>

        </div>
      </div>
    </div>
  );
}
