import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();

  // âœ… include loading from AuthContext
  const { login, signup, isAuthenticated, loading } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [showPass, setShowPass] = useState(false);

  // âœ… WAIT until auth state finishes loading
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-gray-500">Loading...</p>
      </div>
    );
  }

  // âœ… redirect AFTER render (not during render)
  useEffect(() => {
    if (isAuthenticated) {
      navigate(location.state?.from?.pathname || '/');
    }
  }, [isAuthenticated, navigate, location]);

  const validate = () => {
    const errs = {};
    if (!form.email.trim()) errs.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) errs.email = 'Invalid email format';

    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'At least 6 characters';

    if (isSignup && !form.name.trim()) errs.name = 'Name is required';

    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errs = validate();
    if (Object.keys(errs).length) {
      setErrors(errs);
      return;
    }

    setErrors({});
    setSubmitting(true);

    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created! Welcome', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Logged in successfully', 'success');
      }
      navigate('/');
    } catch (err) {
      const msg = err.response?.data?.message || 'Something went wrong';
      showToast(msg, 'error');

      if (msg.toLowerCase().includes('email')) setErrors({ email: msg });
      else if (msg.toLowerCase().includes('pass')) setErrors({ password: msg });
    } finally {
      setSubmitting(false);
    }
  };

  const change = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));

    if (errors[e.target.name]) {
      setErrors((prev) => {
        const n = { ...prev };
        delete n[e.target.name];
        return n;
      });
    }
  };

  const Input = ({ name, label, type = 'text', placeholder, icon, extra }) => (
    <div>
      <label
        htmlFor={name}
        className="block text-sm font-semibold text-ink mb-1.5"
      >
        {label}
      </label>

      <div className="relative">
        {icon && (
          <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400">
            {icon}
          </span>
        )}

        <input
          id={name}
          name={name}
          type={type}
          value={form[name]}
          onChange={change}
          placeholder={placeholder}
          autoComplete={
            name === 'email'
              ? 'email'
              : name === 'password'
              ? 'current-password'
              : 'name'
          }
          className={`w-full ${icon ? 'pl-10' : 'pl-3'} pr-3 py-2.5 rounded-xl border text-sm bg-white text-ink placeholder-gray-400 outline-none transition-all duration-200
          ${
            errors[name]
              ? 'border-red-400 bg-red-50 focus:ring-2 focus:ring-red-200'
              : 'border-gray-200 focus:border-accent focus:ring-2 focus:ring-accent/20'
          }`}
        />

        {extra}
      </div>

      {errors[name] && (
        <p className="mt-1 text-xs text-red-500 font-medium">{errors[name]}</p>
      )}
    </div>
  );

  return (
    <div className="min-h-screen bg-cream flex">
      {/* LEFT PANEL */}
      <div className="hidden lg:flex lg:w-1/2 bg-gradient-to-br from-ink via-gray-900 to-gray-800 p-10 flex-col justify-between">
        <div className="flex items-center gap-2">
          <div className="w-9 h-9 rounded-lg bg-gradient-to-br from-accent to-rose-600 flex items-center justify-center">
            <span className="text-white font-bold text-sm">BF</span>
          </div>
          <span className="font-display text-xl text-white font-bold">
            Learn<span className="text-accent">Hub</span>
          </span>
        </div>

        <p className="text-white/80 text-lg italic">
          The best work you can do is to start today.
        </p>
      </div>

      {/* RIGHT FORM */}
      <div className="w-full lg:w-1/2 flex items-center justify-center px-6 py-12">
        <div className="w-full max-w-md">
          <div className="mb-8">
            <h1 className="font-display text-3xl font-bold text-ink">
              {isSignup ? 'Create account' : 'Nice to see you again'}
            </h1>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {isSignup && (
              <Input name="name" label="Full Name" placeholder="Alice Johnson" icon="ðŸ‘¤" />
            )}

            <Input name="email" label="Email" type="email" placeholder="alice@example.com" icon="âœ‰" />

            <Input
              name="password"
              label="Password"
              type={showPass ? 'text' : 'password'}
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              icon="ðŸ”’"
              extra={
                <button
                  type="button"
                  onClick={() => setShowPass(!showPass)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400"
                >
                  {showPass ? 'Hide' : 'Show'}
                </button>
              }
            />

            <button
              type="submit"
              disabled={submitting}
              className="w-full py-3 rounded-xl bg-gradient-to-r from-accent to-rose-600 text-white font-semibold"
            >
              {submitting ? 'Processing...' : isSignup ? 'Create Account' : 'Sign In'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
