import React, { useState } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();
  const { login, signup, isAuthenticated } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showPass, setShowPass] = useState(false);

  if (isAuthenticated) {
    navigate(location.state?.from?.pathname || '/');
    return null;
  }

  const validate = () => {
    const errs = {};
    if (!form.email.trim()) errs.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) errs.email = 'Invalid email';

    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'Min 6 characters';

    if (isSignup && !form.name.trim()) errs.name = 'Name required';
    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) return setErrors(errs);

    setErrors({});
    setLoading(true);

    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created!', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Logged in successfully', 'success');
      }
      navigate('/');
    } catch (err) {
      const msg = err.response?.data?.message || 'Something went wrong';
      showToast(msg, 'error');
    } finally {
      setLoading(false);
    }
  };

  const change = (e) =>
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const Input = ({ name, label, type, autoComplete, placeholder, icon }) => (
    <div>
      <label htmlFor={name} className="block text-sm font-semibold mb-1">
        {label}
      </label>

      <div className="relative">
        {icon && (
          <span className="absolute left-3 top-1/2 -translate-y-1/2">
            {icon}
          </span>
        )}

        <input
          id={name}
          name={name}
          type={type}
          autoComplete={autoComplete}  // ðŸ”¥ FIX HERE
          value={form[name]}
          onChange={change}
          placeholder={placeholder}
          className="w-full pl-10 pr-3 py-2 rounded-xl border"
        />
      </div>
    </div>
  );

  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="w-full max-w-md">
        <h1 className="text-3xl font-bold mb-6">
          {isSignup ? 'Create account' : 'Login'}
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">

          {isSignup && (
            <Input
              name="name"
              label="Full Name"
              type="text"
              autoComplete="name"
              placeholder="John Doe"
              icon="ðŸ‘¤"
            />
          )}

          <Input
            name="email"
            label="Email"
            type="email"
            autoComplete="email"
            placeholder="john@gmail.com"
            icon="âœ‰"
          />

          <Input
            name="password"
            label="Password"
            type={showPass ? 'text' : 'password'}
            autoComplete={isSignup ? "new-password" : "current-password"}
            placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            icon="ðŸ”’"
          />

          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 bg-blue-600 text-white rounded-xl"
          >
            {loading ? 'Processing...' : isSignup ? 'Sign Up' : 'Login'}
          </button>
        </form>

        <p className="text-center mt-4">
          {isSignup ? 'Already have an account?' : "Don't have an account?"}
          <Link to={isSignup ? '/login' : '/signup'} className="text-blue-600 ml-2">
            {isSignup ? 'Login' : 'Sign up'}
          </Link>
        </p>
      </div>
    </div>
  );
}
