import React, { useState } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();
  const { login, signup, isAuthenticated } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showPass, setShowPass] = useState(false);

  // Redirect if already logged in
  if (isAuthenticated) {
    navigate(location.state?.from?.pathname || '/');
    return null;
  }

  const validate = () => {
    const errs = {};
    if (!form.email.trim())              errs.email    = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) errs.email = 'Invalid email format';
    if (!form.password)                  errs.password = 'Password is required';
    else if (form.password.length < 6)   errs.password = 'At least 6 characters';
    if (isSignup && !form.name.trim())   errs.name     = 'Name is required';
    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) { setErrors(errs); return; }
    setErrors({});
    setLoading(true);
    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created! Welcome ðŸŽ‰', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Logged in successfully âœ“', 'success');
      }
      navigate('/');
    } catch (err) {
      const msg = err.response?.data?.message || 'Something went wrong';
      showToast(msg, 'error');
      // Highlight field if possible
      if (msg.toLowerCase().includes('email'))    setErrors({ email: msg });
      else if (msg.toLowerCase().includes('pass')) setErrors({ password: msg });
    } finally {
      setLoading(false);
    }
  };

  const change = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
    if (errors[e.target.name]) setErrors((prev) => { const n = { ...prev }; delete n[e.target.name]; return n; });
  };

  /* â”€â”€ Input component â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const Input = ({ name, label, type = 'text', placeholder, icon, extra }) => (
    <div>
      <label className="block text-sm font-semibold text-ink mb-1.5">{label}</label>
      <div className="relative">
        {icon && <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400">{icon}</span>}
        <input
          name={name}
          type={type}
          value={form[name]}
          onChange={change}
          placeholder={placeholder}
          className={`w-full ${icon ? 'pl-10' : 'pl-3'} pr-3 py-2.5 rounded-xl border text-sm bg-white text-ink placeholder-gray-400 outline-none transition-all duration-200
            ${errors[name] ? 'border-red-400 bg-red-50 focus:ring-2 focus:ring-red-200' : 'border-gray-200 focus:border-accent focus:ring-2 focus:ring-accent/20'}`}
        />
        {extra}
      </div>
      {errors[name] && <p className="mt-1 text-xs text-red-500 font-medium">{errors[name]}</p>}
    </div>
  );

  return (
    <div className="min-h-screen bg-cream flex">
      {/* â”€â”€ Left decorative panel â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */}
      <div className="hidden lg:flex lg:w-1/2 relative bg-gradient-to-br from-ink via-gray-900 to-gray-800 p-10 flex-col justify-between overflow-hidden">
        {/* Decorative circles */}
        <div className="absolute -top-24 -left-24 w-64 h-64 rounded-full bg-accent/10 blur-3xl" />
        <div className="absolute bottom-20 right-10 w-48 h-48 rounded-full bg-gold/10 blur-2xl" />
        <div className="absolute top-1/2 -right-10 w-32 h-32 rounded-full border-2 border-white/10" />

        {/* Logo */}
        <div className="flex items-center gap-2 relative z-10">
          <div className="w-9 h-9 rounded-lg bg-gradient-to-br from-accent to-rose-600 flex items-center justify-center">
            <span className="text-white font-bold text-sm">BF</span>
          </div>
          <span className="font-display text-xl text-white font-bold">Learn<span className="text-accent">Hub</span></span>
        </div>

        {/* Testimonial-style quote */}
        <div className="relative z-10">
          <div className="text-5xl text-accent/40 font-display leading-none mb-3">"</div>
          <p className="text-white/80 text-lg font-light leading-relaxed italic">
            The best time to start learning was yesterday. The second best time is right now.
          </p>
          <div className="mt-6 flex items-center gap-3">
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-accent to-rose-500 flex items-center justify-center text-white font-bold text-sm">SC</div>
            <div>
              <p className="text-white text-sm font-semibold">Steve C.</p>
              <p className="text-white/50 text-xs">Senior Developer</p>
            </div>
          </div>
        </div>

        {/* Stats */}
        <div className="relative z-10 flex gap-8">
          {[['2,400+','Students'], ['15+','Courses'], ['4.9â˜…','Rating']].map(([val, lbl]) => (
            <div key={lbl}>
              <p className="text-white text-xl font-bold">{val}</p>
              <p className="text-white/40 text-xs">{lbl}</p>
            </div>
          ))}
        </div>
      </div>

      {/* â”€â”€ Right form panel â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */}
      <div className="w-full lg:w-1/2 flex items-center justify-center px-6 py-12">
        <div className="w-full max-w-md">
          {/* Header */}
          <div className="mb-8">
            <h1 className="font-display text-3xl font-bold text-ink">
              {isSignup ? 'Create account' : 'Welcome back'}
            </h1>
            <p className="text-muted text-sm mt-1.5">
              {isSignup ? 'Join thousands of learners today.' : 'Sign in to continue your learning journey.'}
            </p>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            {isSignup && <Input name="name" label="Full Name" placeholder="Alice Johnson" icon="ðŸ‘¤" />}
            <Input name="email" label="Email" type="email" placeholder="alice@example.com" icon="âœ‰" />
            <Input
              name="password"
              label="Password"
              type={showPass ? 'text' : 'password'}
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              icon="ðŸ”’"
              extra={
                <button type="button" onClick={() => setShowPass(!showPass)} className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-accent transition-colors text-sm">
                  {showPass ? 'Hide' : 'Show'}
                </button>
              }
            />

            <button
              type="submit"
              disabled={loading}
              className="w-full py-3 rounded-xl bg-gradient-to-r from-accent to-rose-600 text-white font-semibold text-sm shadow-md hover:shadow-lg hover:from-rose-600 hover:to-accent transition-all duration-200 disabled:opacity-60 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            >
              {loading ? (
                <><span className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" /> Processingâ€¦</>
              ) : (
                isSignup ? 'Create Account' : 'Sign In'
              )}
            </button>
          </form>

          {/* Divider */}
          <div className="flex items-center gap-3 my-5">
            <div className="flex-1 h-px bg-gray-200" />
            <span className="text-xs text-gray-400 font-medium">OR</span>
            <div className="flex-1 h-px bg-gray-200" />
          </div>

          {/* Demo credentials */}
          <div className="bg-surface rounded-xl p-4 mb-5">
            <p className="text-xs text-muted font-semibold mb-2 uppercase tracking-wide">ðŸ‘€ Demo Credentials</p>
            <div className="space-y-1">
              {[['alice@example.com','password123'],['bob@example.com','password123'],['charlie@example.com','password123']].map(([email]) => (
                <button
                  key={email}
                  type="button"
                  onClick={() => { setForm({ name: '', email, password: 'password123' }); setErrors({}); }}
                  className="w-full text-left text-xs text-muted hover:text-accent transition-colors px-2 py-0.5 rounded hover:bg-white"
                >
                  {email} / password123
                </button>
              ))}
            </div>
          </div>

          {/* Switch link */}
          <p className="text-center text-sm text-muted">
            {isSignup ? 'Already have an account? ' : "Don't have an account? "}
            <Link to={isSignup ? '/login' : '/signup'} className="text-accent font-semibold hover:underline">
              {isSignup ? 'Log in' : 'Sign up'}
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
}
