import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();
  const { login, signup, isAuthenticated } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  // ✅ SAFE redirect
  useEffect(() => {
    if (isAuthenticated) {
      navigate(location.state?.from?.pathname || '/');
    }
  }, [isAuthenticated, navigate, location]);

  const validate = () => {
    const e = {};
    if (!form.email.trim()) e.email = 'Email required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) e.email = 'Invalid email';

    if (!form.password) e.password = 'Password required';
    else if (form.password.length < 6) e.password = 'Min 6 characters';

    if (isSignup && !form.name.trim()) e.name = 'Name required';
    return e;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) { setErrors(errs); return; }

    setErrors({});
    setLoading(true);

    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created!', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Welcome back', 'success');
      }
      navigate('/');
    } catch (err) {
      showToast(err.response?.data?.message || 'Error', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ minHeight:'100vh', background:'#E8E0D5', display:'flex', alignItems:'center', justifyContent:'center', padding:'80px 24px' }}>
      <div style={{ width:'100%', maxWidth:420 }}>

        {/* HEADER */}
        <div style={{ textAlign:'center', marginBottom:40 }}>
          <Link to="/" style={{ display:'inline-block', marginBottom:24 }}>
            <div style={{ width:48, height:48, background:'#C0564A', display:'flex', alignItems:'center', justifyContent:'center', margin:'0 auto' }}>
              <span style={{ color:'#fff', fontFamily:"'Merriweather',serif", fontWeight:700, fontSize:24 }}>LH</span>
            </div>
          </Link>

          <h1 style={{ fontFamily:"'Merriweather',serif", fontSize:42, fontWeight:600, color:'#2C1A14', marginBottom:8 }}>
            {isSignup ? 'Create Account' : 'Welcome Back'}
          </h1>
          <p style={{ color:'#7A6558', fontSize:14 }}>
            {isSignup ? 'Join us today' : 'Sign in to continue'}
          </p>
        </div>

        {/* FORM */}
        <form onSubmit={handleSubmit} style={{ background:'#fff', padding:40 }}>
          {isSignup && (
            <div style={{ marginBottom:24 }}>
              <label style={{ display:'block', fontSize:12, fontWeight:500, marginBottom:8 }}>Name</label>
              <input
                value={form.name}
                onChange={e => setForm(p => ({ ...p, name: e.target.value }))}
                style={{ width:'100%', padding:'12px 14px', border:'1px solid #C8BFB5', background:'#F5F1EC' }}
                placeholder="Alice Johnson"
              />
              {errors.name && <p style={{ color:'#C0564A', fontSize:12 }}>{errors.name}</p>}
            </div>
          )}

          <div style={{ marginBottom:24 }}>
            <label style={{ fontSize:12, fontWeight:500 }}>Email</label>
            <input
              type="email"
              value={form.email}
              onChange={e => setForm(p => ({ ...p, email: e.target.value }))}
              style={{ width:'100%', padding:'12px 14px', border:'1px solid #C8BFB5', background:'#F5F1EC' }}
            />
          </div>

          <div style={{ marginBottom:32 }}>
            <label style={{ fontSize:12, fontWeight:500 }}>Password</label>
            <input
              type="password"
              value={form.password}
              onChange={e => setForm(p => ({ ...p, password: e.target.value }))}
              style={{ width:'100%', padding:'12px 14px', border:'1px solid #C8BFB5', background:'#F5F1EC' }}
            />
          </div>

          <button type="submit" disabled={loading} style={{ width:'100%', background:'#3B1F1A', color:'#fff', padding:'14px 0' }}>
            {loading ? 'Processing...' : isSignup ? 'Create Account' : 'Sign In'}
          </button>
        </form>

        {/* DEMO ACCOUNTS */}
        <div style={{ marginTop:24, background:'#fff', padding:20 }}>
          <p style={{ fontSize:11, fontWeight:600, marginBottom:12 }}>Demo Accounts</p>
          {[
            ['monu@gmail.com', 'pass@4647'],
            ['sanjay@gmail.com', 'pass@777'],
            ['manij@gmail.com', 'pass@7878']
          ].map(([email, password]) => (
            <button
              key={email}
              type="button"
              onClick={() => setForm({ name: '', email, password })}
              style={{ display:'block', width:'100%', textAlign:'left', background:'none', border:'none' }}
            >
              {email} / {password}
            </button>
          ))}
        </div>

        {/* SWITCH */}
        <p style={{ marginTop:24, textAlign:'center' }}>
          {isSignup ? 'Already have an account?' : "Don't have an account?"}{' '}
          <Link to={isSignup ? '/login' : '/signup'} style={{ color:'#C0564A', fontWeight:600 }}>
            {isSignup ? 'Log in' : 'Sign up'}
          </Link>
        </p>

      </div>
    </div>
  );
}
