import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();

  const { login, signup, isAuthenticated, loading } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [showPass, setShowPass] = useState(false);

  // ✅ hook ALWAYS at top
  useEffect(() => {
    if (!loading && isAuthenticated) {
      navigate(location.state?.from?.pathname || '/');
    }
  }, [loading, isAuthenticated, navigate, location]);

  // validation
  const validate = () => {
    const errs = {};
    if (!form.email.trim()) errs.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) errs.email = 'Invalid email format';

    if (!form.password) errs.password = 'Password is required';
    else if (form.password.length < 6) errs.password = 'At least 6 characters';

    if (isSignup && !form.name.trim()) errs.name = 'Name is required';

    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errs = validate();
    if (Object.keys(errs).length) {
      setErrors(errs);
      return;
    }

    setErrors({});
    setSubmitting(true);

    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created! Welcome', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Logged in successfully', 'success');
      }
      navigate('/');
    } catch (err) {
      const msg = err.response?.data?.message || 'Something went wrong';
      showToast(msg, 'error');

      if (msg.toLowerCase().includes('email')) setErrors({ email: msg });
      else if (msg.toLowerCase().includes('pass')) setErrors({ password: msg });
    } finally {
      setSubmitting(false);
    }
  };

  const change = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));

    if (errors[e.target.name]) {
      setErrors((prev) => {
        const n = { ...prev };
        delete n[e.target.name];
        return n;
      });
    }
  };

  const Input = ({ name, label, type = 'text', placeholder }) => (
    <div>
      <label className="block text-sm font-semibold mb-1">{label}</label>
      <input
        name={name}
        type={type}
        value={form[name]}
        onChange={change}
        placeholder={placeholder}
        autoComplete={
          name === 'email'
            ? 'email'
            : name === 'password'
            ? 'current-password'
            : 'name'
        }
        className="w-full border px-3 py-2 rounded"
      />
      {errors[name] && (
        <p className="text-red-500 text-xs mt-1">{errors[name]}</p>
      )}
    </div>
  );

  // ✅ AFTER hooks → conditional UI allowed
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading...
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="w-full max-w-md">
        <h1 className="text-2xl font-bold mb-4">
          {isSignup ? 'Create account' : 'Login'}
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          {isSignup && (
            <Input name="name" label="Name" placeholder="John Doe" />
          )}

          <Input name="email" label="Email" type="email" placeholder="email@gmail.com" />

          <Input
            name="password"
            label="Password"
            type={showPass ? 'text' : 'password'}
            placeholder="******"
          />

          <button
            type="button"
            onClick={() => setShowPass(!showPass)}
            className="text-sm text-blue-500"
          >
            {showPass ? 'Hide Password' : 'Show Password'}
          </button>

          <button
            type="submit"
            disabled={submitting}
            className="w-full bg-blue-600 text-white py-2 rounded"
          >
            {submitting ? 'Processing...' : 'Submit'}
          </button>
        </form>
      </div>
    </div>
  );
}
