import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { showToast } from '../utils/toast';

export default function AuthPage({ mode = 'login' }) {
  const isSignup = mode === 'signup';
  const navigate = useNavigate();
  const location = useLocation();
  const { login, signup, isAuthenticated } = useAuth();

  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isAuthenticated) {
      navigate(location.state?.from?.pathname || '/');
    }
  }, [isAuthenticated, navigate, location]);

  const validate = () => {
    const e = {};
    if (!form.email.trim()) e.email = 'Email required';
    else if (!/\S+@\S+\.\S+/.test(form.email)) e.email = 'Invalid email';

    if (!form.password) e.password = 'Password required';
    else if (form.password.length < 6) e.password = 'Min 6 characters';

    if (isSignup && !form.name.trim()) e.name = 'Name required';
    return e;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errs = validate();
    if (Object.keys(errs).length) { setErrors(errs); return; }

    setErrors({});
    setLoading(true);

    try {
      if (isSignup) {
        await signup(form.name.trim(), form.email.trim(), form.password);
        showToast('Account created!', 'success');
      } else {
        await login(form.email.trim(), form.password);
        showToast('Welcome back', 'success');
      }
      navigate('/');
    } catch (err) {
      showToast(err.response?.data?.message || 'Error', 'error');
    } finally {
      setLoading(false);
    }
  };

  const updateField = (name, value) => {
    setForm((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => { const n = { ...prev }; delete n[name]; return n; });
  };

  return (
    <div className="min-h-screen grid lg:grid-cols-2">

      {/* LEFT SPLIT PANEL */}
      <div className="hidden lg:flex flex-col justify-between p-12 bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
        <div>
          <h2 className="text-3xl font-bold mb-2">LearnHub</h2>
          <p className="text-gray-400">Start your learning journey today.</p>
        </div>

        <div className="flex gap-10">
          {[
            ['2,400+','Students'],
            ['15+','Courses'],
            ['4.9★','Rating']
          ].map(([v,l]) => (
            <div key={l}>
              <p className="text-xl font-bold">{v}</p>
              <p className="text-xs text-gray-400">{l}</p>
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT FORM */}
      <div className="flex items-center justify-center bg-gray-50 px-6 py-12">

        <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl">

          <h1 className="text-3xl font-bold mb-6 text-gray-800">
            {isSignup ? 'Create Account' : 'Welcome Back'}
          </h1>

          <form onSubmit={handleSubmit} className="space-y-4">

            {isSignup && (
              <div>
                <label className="text-sm font-semibold">Name</label>
                <input
                  value={form.name}
                  onChange={e => updateField('name', e.target.value)}
                  className="w-full border px-4 py-3 rounded-lg mt-1"
                  placeholder="Alice Johnson"
                />
                {errors.name && <p className="text-red-500 text-xs">{errors.name}</p>}
              </div>
            )}

            <div>
              <label className="text-sm font-semibold">Email</label>
              <input
                type="email"
                value={form.email}
                onChange={e => updateField('email', e.target.value)}
                className="w-full border px-4 py-3 rounded-lg mt-1"
                placeholder="alice@example.com"
              />
              {errors.email && <p className="text-red-500 text-xs">{errors.email}</p>}
            </div>

            <div>
              <label className="text-sm font-semibold">Password</label>
              <input
                type="password"
                value={form.password}
                onChange={e => updateField('password', e.target.value)}
                className="w-full border px-4 py-3 rounded-lg mt-1"
                placeholder="••••••••"
              />
              {errors.password && <p className="text-red-500 text-xs">{errors.password}</p>}
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold"
            >
              {loading ? 'Processing...' : isSignup ? 'Create Account' : 'Sign In'}
            </button>

          </form>

          {/* DEMO CREDENTIALS */}
          <div className="mt-6 border-t pt-4">
            <p className="text-xs font-bold mb-2 text-gray-500 uppercase">Demo Accounts</p>

            {[
              ['monu@gmail.com', 'pass@4647'],
              ['sanjay@gmail.com', 'pass@777'],
              ['manij@gmail.com', 'pass@7878']
            ].map(([email, password]) => (
              <button
                key={email}
                type="button"
                onClick={() => setForm({ name: '', email, password })}
                className="block w-full text-left text-sm py-1 hover:text-blue-600"
              >
                {email} / {password}
              </button>
            ))}
          </div>

        </div>
      </div>
    </div>
  );
}
